actionConfiguration.body:
() => {
  if (DiscordSwitch.isSwitchedOn) {
    DiscordAPI.run();
    const successMessage1 = ("Message sent successfully to ").concat(List1.selectedItem.MailingList).concat("server");
    showAlert(successMessage1, "success");
  }
  if (EmailSwitch.isSwitchedOn) {
    SendEmail.run();
    const successMessage2 = ("Mail sent succesfully to ").concat(List1.selectedItem.MailingList).concat("list");
    showAlert(successMessage2, "success");
  }
  resetWidget("SendMessage_Modal");
  closeModal("SendMessage_Modal");
}

